package typeCasting;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		double myDouble =99.00001;
		int myInt = (int)myDouble;
		
		System.out.println(myDouble);
		System.out.println(myInt);
		
		
		int intA = 12;
		short shortA = (short)intA;
	     System.out.println(intA);
	     System.out.println(shortA);
	     
	     
	     
	     float myFloat = 12345.776f;
	     double myDoubleA = (double)myFloat;
	     System.out.println(myFloat);
	     System.out.println(myDoubleA);//there is difference in the result search the google  about this ....
	     
	    		 

	}

}
